const RE_MONTH = pattern`(?<month>[0-9]{2})`;
const RE_DAY = pattern`(?<day>[0-9]{2})`;
const RE_DATE = regex('g')`
 ${RE_YEAR} # 4 digits
 -
 ${RE_MONTH} # 2 digits
 -
 ${RE_DAY} # 2 digits
`;

const match = RE_DATE.exec('2017-01-27');
assert.equal(match.groups.year, '2017');

