function main(args) {
  const command = args[0];
  if (command === undefined) {
    throw new Error();
  }
  switch (command) {
    case 'once':
      const text = args[1];
      if (text === undefined) {
        throw new Error();
      }
      return text;
    case 'repeat':
      const timesStr = args[1];
      const text = args[2]; // (A)
      if (timesStr === undefined || text === undefined) {
        throw new Error();
      }
      const times = Number(timesStr);
      return text.repeat(times);
    default:
      throw new Error();
  }
}

