```ts
type WithKey = {
 key: string,
};
function addKey<Obj extends object>(obj: Obj, key: string)
 : Obj & WithKey
{
 const objWithKey = obj as (Obj & WithKey);
 objWithKey.key = key;
 return objWithKey;
}

```

```ts
const paris = {
 city: 'Paris',
};

const parisWithKey = addKey(paris, 'paris');
assertType<
{ 
 city: string,
 key: string,
}
>(parisWithKey);

```
